// Stub
export function getHash(data: string): string {
    return data
}

export function getCurrentJWT(): string {
    return window.localStorage.getItem('accessToken') || ""
}

// Backend use JWT authorization as bearer token, i.e. request headers must contain {"Authorization": "Bearer {JWT}"}
export function getAuthToken(): string {
    return "Bearer " + getCurrentJWT()
}
