import { HttpClient, HttpHeaders } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable, Observer, retry, Subscriber } from 'rxjs';
import { getAuthToken } from 'src/utils';
import { IOrder, OrderStatus } from '../models/order';

@Injectable({
  providedIn: 'root'
})
export class OrderService {
  orders: IOrder[] = []

  httpOptions = {
    headers: new HttpHeaders({
      'Content-Type': 'application/json',
      Authorization: ''
    })
  }

  constructor(private http: HttpClient) {}

  // Fetch amount orders from API
  getOrders(offset: number, amount: number): Observable<IOrder[]> {
    console.log(this.httpOptions.headers.append('Authorization', getAuthToken()))

    return this.http.get<IOrder[]>(
      `api/v1/order?offset=${offset}&amount=${amount}`,
      this.httpOptions
    ).pipe(retry(2))
  }

  updateStatus(orderID: string, status: OrderStatus) {
    this.httpOptions.headers.set('Authorization', getAuthToken())
    return this.http.patch<IOrder>(
      `api/v1/order/${orderID}`,
      {
        status
      },
      this.httpOptions
    ).pipe(retry(2))
  }
}
